package tictactoeboardwinner ; public class TicTacToeBoardWinnerRun { public static void main(String[] args) { Board b = new Board(new Marker[][] {  { Marker.X, Marker.X, Marker.X } , {Marker.EMPTY, Marker.EMPTY, Marker.EMPTY } , {Marker.O, Marker.O, Marker.O} } ) ;   b.Draw() ;  System.out.printf("\n%s is a winner : %s \n",Marker.X, b.Winner(Marker.X)) ;  System.out.printf("%s is a winner : %s \n",Marker.O, b.Winner(Marker.O)) ;   } }