package org.imogene.epicam.server.locator ; public class CasTuberculoseLocator extends Locator<CasTuberculose, String> { private CasTuberculoseHandler handler ;  public CasTuberculoseLocator() { }  @Override public CasTuberculose create(Class<? extends CasTuberculose> clazz) { return new CasTuberculose() ;  }  @Override public CasTuberculose find(Class<? extends CasTuberculose> clazz, String id) { if (handler == null) initHandler() ;  return handler.getById(id) ;  }  @Override public Class<CasTuberculose> getDomainType() { return CasTuberculose.class ;  }  @Override public String getId(CasTuberculose domainObject) { return domainObject.getId() ;  }  @Override public Class<String> getIdType() { return String.class ;  }  @Override public Object getVersion(CasTuberculose domainObject) { return domainObject.getVersion() ;  }  private void initHandler() { HttpServletRequest request = RequestFactoryServlet .getThreadLocalRequest() ;  ApplicationContext context = WebApplicationContextUtils .getWebApplicationContext(request.getSession() .getServletContext()) ;  handler = (CasTuberculoseHandler) context .getBean("casTuberculoseHandler") ;  } }