package org.imogene.epicam.domain.entity.backup ; public class CommandeCloner { public static CommandeBck cloneEntity(Commande entity) { CommandeBck bck = new CommandeBck() ;  bck.setTraceId(UUID.randomUUID().toString()) ;  bck.setId(entity.getId()) ;  bck.setCreated(entity.getCreated()) ;  bck.setCreatedBy(entity.getCreatedBy()) ;  bck.setModified(entity.getModified()) ;  bck.setModifiedBy(entity.getModifiedBy()) ;  bck.setModifiedFrom(entity.getModifiedFrom()) ;  bck.setUploadDate(entity.getUploadDate()) ;  bck.setDeleted(entity.getDeleted()) ;  bck.setVersion(entity.getVersion()) ;  bck.setDate(entity.getDate()) ;  if (entity.getRegion() != null) { bck.setRegion(entity.getRegion().getId()) ;  }  if (entity.getDistrictSante() != null) { bck.setDistrictSante(entity.getDistrictSante().getId()) ;  }  if (entity.getCDT() != null) { bck.setCDT(entity.getCDT().getId()) ;  }  if (entity.getMedicaments() != null) { StringBuilder builder = new StringBuilder() ;  for (DetailCommandeMedicament i : entity.getMedicaments()) { builder.append(i.getId()).append(" ; ") ;  }  bck.setMedicaments(builder.toString()) ;  }  if (entity.getIntrants() != null) { StringBuilder builder = new StringBuilder() ;  for (DetailCommandeIntrant i : entity.getIntrants()) { builder.append(i.getId()).append(" ; ") ;  }  bck.setIntrants(builder.toString()) ;  }  bck.setApprouveeRegion(entity.getApprouveeRegion()) ;  bck.setMotifRejetRegion(entity.getMotifRejetRegion()) ;  bck.setApprouveeGTC(entity.getApprouveeGTC()) ;  bck.setMotifRejetGTC(entity.getMotifRejetGTC()) ;  return bck ;  } }