package org.imogene.epicam.server.locator ; public class DepartPersonnelLocator extends Locator<DepartPersonnel, String> { private DepartPersonnelHandler handler ;  public DepartPersonnelLocator() { }  @Override public DepartPersonnel create(Class<? extends DepartPersonnel> clazz) { return new DepartPersonnel() ;  }  @Override public DepartPersonnel find(Class<? extends DepartPersonnel> clazz, String id) { if (handler == null) initHandler() ;  return handler.getById(id) ;  }  @Override public Class<DepartPersonnel> getDomainType() { return DepartPersonnel.class ;  }  @Override public String getId(DepartPersonnel domainObject) { return domainObject.getId() ;  }  @Override public Class<String> getIdType() { return String.class ;  }  @Override public Object getVersion(DepartPersonnel domainObject) { return domainObject.getVersion() ;  }  private void initHandler() { HttpServletRequest request = RequestFactoryServlet .getThreadLocalRequest() ;  ApplicationContext context = WebApplicationContextUtils .getWebApplicationContext(request.getSession() .getServletContext()) ;  handler = (DepartPersonnelHandler) context .getBean("departPersonnelHandler") ;  } }