package org.imogene.epicam.server.locator ; public class DistrictSanteLocator extends Locator<DistrictSante, String> { private DistrictSanteHandler handler ;  public DistrictSanteLocator() { }  @Override public DistrictSante create(Class<? extends DistrictSante> clazz) { return new DistrictSante() ;  }  @Override public DistrictSante find(Class<? extends DistrictSante> clazz, String id) { if (handler == null) initHandler() ;  return handler.getById(id) ;  }  @Override public Class<DistrictSante> getDomainType() { return DistrictSante.class ;  }  @Override public String getId(DistrictSante domainObject) { return domainObject.getId() ;  }  @Override public Class<String> getIdType() { return String.class ;  }  @Override public Object getVersion(DistrictSante domainObject) { return domainObject.getVersion() ;  }  private void initHandler() { HttpServletRequest request = RequestFactoryServlet .getThreadLocalRequest() ;  ApplicationContext context = WebApplicationContextUtils .getWebApplicationContext(request.getSession() .getServletContext()) ;  handler = (DistrictSanteHandler) context .getBean("districtSanteHandler") ;  } }