package org.imogene.epicam.server.locator ; public class ExamenATBLocator extends Locator<ExamenATB, String> { private ExamenATBHandler handler ;  public ExamenATBLocator() { }  @Override public ExamenATB create(Class<? extends ExamenATB> clazz) { return new ExamenATB() ;  }  @Override public ExamenATB find(Class<? extends ExamenATB> clazz, String id) { if (handler == null) initHandler() ;  return handler.getById(id) ;  }  @Override public Class<ExamenATB> getDomainType() { return ExamenATB.class ;  }  @Override public String getId(ExamenATB domainObject) { return domainObject.getId() ;  }  @Override public Class<String> getIdType() { return String.class ;  }  @Override public Object getVersion(ExamenATB domainObject) { return domainObject.getVersion() ;  }  private void initHandler() { HttpServletRequest request = RequestFactoryServlet .getThreadLocalRequest() ;  ApplicationContext context = WebApplicationContextUtils .getWebApplicationContext(request.getSession() .getServletContext()) ;  handler = (ExamenATBHandler) context.getBean("examenATBHandler") ;  } }