package org.imogene.web.server.locator ; public class FieldGroupLocator extends Locator<FieldGroup, String> { private FieldGroupHandler handler ;  public FieldGroupLocator() { }  @Override public FieldGroup create(Class<? extends FieldGroup> clazz) { return new FieldGroup() ;  }  @Override public FieldGroup find(Class<? extends FieldGroup> clazz, String id) { if (handler == null) initHandler() ;  return handler.getById(id) ;  }  @Override public Class<FieldGroup> getDomainType() { return FieldGroup.class ;  }  @Override public String getId(FieldGroup domainObject) { return domainObject.getId() ;  }  @Override public Class<String> getIdType() { return String.class ;  }  @Override public Object getVersion(FieldGroup domainObject) { return domainObject.getVersion() ;  }  private void initHandler() { HttpServletRequest request = RequestFactoryServlet.getThreadLocalRequest() ;  ApplicationContext context = WebApplicationContextUtils.getWebApplicationContext(request.getSession() .getServletContext()) ;  handler = (FieldGroupHandler) context.getBean("fieldGroupHandler") ;  } }