package org.imogene.epicam.server.locator ; public class IntrantLocator extends Locator<Intrant, String> { private IntrantHandler handler ;  public IntrantLocator() { }  @Override public Intrant create(Class<? extends Intrant> clazz) { return new Intrant() ;  }  @Override public Intrant find(Class<? extends Intrant> clazz, String id) { if (handler == null) initHandler() ;  return handler.getById(id) ;  }  @Override public Class<Intrant> getDomainType() { return Intrant.class ;  }  @Override public String getId(Intrant domainObject) { return domainObject.getId() ;  }  @Override public Class<String> getIdType() { return String.class ;  }  @Override public Object getVersion(Intrant domainObject) { return domainObject.getVersion() ;  }  private void initHandler() { HttpServletRequest request = RequestFactoryServlet .getThreadLocalRequest() ;  ApplicationContext context = WebApplicationContextUtils .getWebApplicationContext(request.getSession() .getServletContext()) ;  handler = (IntrantHandler) context.getBean("intrantHandler") ;  } }