package org.imogene.epicam.domain.entity.backup ; public class InventaireCloner { public static InventaireBck cloneEntity(Inventaire entity) { InventaireBck bck = new InventaireBck() ;  bck.setTraceId(UUID.randomUUID().toString()) ;  bck.setId(entity.getId()) ;  bck.setCreated(entity.getCreated()) ;  bck.setCreatedBy(entity.getCreatedBy()) ;  bck.setModified(entity.getModified()) ;  bck.setModifiedBy(entity.getModifiedBy()) ;  bck.setModifiedFrom(entity.getModifiedFrom()) ;  bck.setUploadDate(entity.getUploadDate()) ;  bck.setDeleted(entity.getDeleted()) ;  bck.setVersion(entity.getVersion()) ;  bck.setDate(entity.getDate()) ;  if (entity.getRegion() != null) { bck.setRegion(entity.getRegion().getId()) ;  }  if (entity.getDistrictSante() != null) { bck.setDistrictSante(entity.getDistrictSante().getId()) ;  }  if (entity.getCDT() != null) { bck.setCDT(entity.getCDT().getId()) ;  }  if (entity.getDetails() != null) { StringBuilder builder = new StringBuilder() ;  for (DetailInventaire i : entity.getDetails()) { builder.append(i.getId()).append(" ; ") ;  }  bck.setDetails(builder.toString()) ;  }  return bck ;  } }