package org.imogene.epicam.server.locator ; public class LieuDitLocator extends Locator<LieuDit, String> { private LieuDitHandler handler ;  public LieuDitLocator() { }  @Override public LieuDit create(Class<? extends LieuDit> clazz) { return new LieuDit() ;  }  @Override public LieuDit find(Class<? extends LieuDit> clazz, String id) { if (handler == null) initHandler() ;  return handler.getById(id) ;  }  @Override public Class<LieuDit> getDomainType() { return LieuDit.class ;  }  @Override public String getId(LieuDit domainObject) { return domainObject.getId() ;  }  @Override public Class<String> getIdType() { return String.class ;  }  @Override public Object getVersion(LieuDit domainObject) { return domainObject.getVersion() ;  }  private void initHandler() { HttpServletRequest request = RequestFactoryServlet .getThreadLocalRequest() ;  ApplicationContext context = WebApplicationContextUtils .getWebApplicationContext(request.getSession() .getServletContext()) ;  handler = (LieuDitHandler) context.getBean("lieuDitHandler") ;  } }