package org.imogene.epicam.client.event.list ; public class ListMedicamentEvent extends GwtEvent<ListMedicamentEvent.Handler> { public static final Type<Handler> TYPE = new Type<Handler>() ;  private String searchText = null ;  public interface Handler extends EventHandler { void listMedicament() ;  void listMedicament(String searchText) ;  }  public ListMedicamentEvent() { }  public ListMedicamentEvent(String searchText) { this.searchText = searchText ;  }  @Override protected void dispatch(Handler handler) { if (searchText == null) handler.listMedicament() ;  else handler.listMedicament(searchText) ;  }  @Override public com.google.gwt.event.shared.GwtEvent.Type<Handler> getAssociatedType() { return TYPE ;  } }