package org.imogene.admin.client.event.list ; public class ListProfileEvent extends GwtEvent<ListProfileEvent.Handler> { public static final Type<Handler> TYPE = new Type<Handler>() ;  private String searchText = null ;  public interface Handler extends EventHandler { void listProfile() ;  void listProfile(String searchText) ;  }  public ListProfileEvent() { }  public ListProfileEvent(String searchText) { this.searchText = searchText ;  }  @Override protected void dispatch(Handler handler) { if (searchText == null) handler.listProfile() ;  else handler.listProfile(searchText) ;  }  @Override public com.google.gwt.event.shared.GwtEvent.Type<Handler> getAssociatedType() { return TYPE ;  } }