package org.imogene.epicam.client.event.list ; public class ListRegimeEvent extends GwtEvent<ListRegimeEvent.Handler> { public static final Type<Handler> TYPE = new Type<Handler>() ;  private String searchText = null ;  public interface Handler extends EventHandler { void listRegime() ;  void listRegime(String searchText) ;  }  public ListRegimeEvent() { }  public ListRegimeEvent(String searchText) { this.searchText = searchText ;  }  @Override protected void dispatch(Handler handler) { if (searchText == null) handler.listRegime() ;  else handler.listRegime(searchText) ;  }  @Override public com.google.gwt.event.shared.GwtEvent.Type<Handler> getAssociatedType() { return TYPE ;  } }