package org.imogene.epicam.domain.entity.backup ; public class OutBoxCloner { public static OutBoxBck cloneEntity(OutBox entity) { OutBoxBck bck = new OutBoxBck() ;  bck.setTraceId(UUID.randomUUID().toString()) ;  bck.setId(entity.getId()) ;  bck.setCreated(entity.getCreated()) ;  bck.setCreatedBy(entity.getCreatedBy()) ;  bck.setModified(entity.getModified()) ;  bck.setModifiedBy(entity.getModifiedBy()) ;  bck.setModifiedFrom(entity.getModifiedFrom()) ;  bck.setUploadDate(entity.getUploadDate()) ;  bck.setDeleted(entity.getDeleted()) ;  bck.setVersion(entity.getVersion()) ;  if (entity.getPatient() != null) { bck.setPatient(entity.getPatient().getId()) ;  }  bck.setMessage(entity.getMessage()) ;  bck.setReponse(entity.getReponse()) ;  bck.setStatut(entity.getStatut()) ;  bck.setDateDernierEssai(entity.getDateDernierEssai()) ;  return bck ;  } }