package org.imogene.web.server.locator ; public class ProfileLocator extends Locator<Profile, String> { private ProfileHandler handler ;  public ProfileLocator() { }  @Override public Profile create(Class<? extends Profile> clazz) { return new Profile() ;  }  @Override public Profile find(Class<? extends Profile> clazz, String id) { if (handler == null) initHandler() ;  return handler.getById(id) ;  }  @Override public Class<Profile> getDomainType() { return Profile.class ;  }  @Override public String getId(Profile domainObject) { return domainObject.getId() ;  }  @Override public Class<String> getIdType() { return String.class ;  }  @Override public Object getVersion(Profile domainObject) { return domainObject.getVersion() ;  }  private void initHandler() { HttpServletRequest request = RequestFactoryServlet.getThreadLocalRequest() ;  ApplicationContext context = WebApplicationContextUtils.getWebApplicationContext(request.getSession() .getServletContext()) ;  handler = (ProfileHandler) context.getBean("profileHandler") ;  } }