package org.imogene.epicam.server.locator ; public class RegionLocator extends Locator<Region, String> { private RegionHandler handler ;  public RegionLocator() { }  @Override public Region create(Class<? extends Region> clazz) { return new Region() ;  }  @Override public Region find(Class<? extends Region> clazz, String id) { if (handler == null) initHandler() ;  return handler.getById(id) ;  }  @Override public Class<Region> getDomainType() { return Region.class ;  }  @Override public String getId(Region domainObject) { return domainObject.getId() ;  }  @Override public Class<String> getIdType() { return String.class ;  }  @Override public Object getVersion(Region domainObject) { return domainObject.getVersion() ;  }  private void initHandler() { HttpServletRequest request = RequestFactoryServlet .getThreadLocalRequest() ;  ApplicationContext context = WebApplicationContextUtils .getWebApplicationContext(request.getSession() .getServletContext()) ;  handler = (RegionHandler) context.getBean("regionHandler") ;  } }