package org.imogene.web.client.event ; public class SaveDynamicFieldTemplateEvent extends GwtEvent<SaveDynamicFieldTemplateEvent.Handler> { public static final Type<Handler> TYPE = new Type<Handler>() ;  public interface Handler extends EventHandler { void saveDynamicFieldTemplate(DynamicFieldTemplateProxy value) ;  void saveDynamicFieldTemplate(DynamicFieldTemplateProxy value, String initField) ;  }  private final DynamicFieldTemplateProxy value ;  private final String initField ;  public SaveDynamicFieldTemplateEvent(DynamicFieldTemplateProxy value) { this(value, null) ;  }  public SaveDynamicFieldTemplateEvent(DynamicFieldTemplateProxy value, String initField) { this.value = value ;  this.initField = initField ;  }  @Override protected void dispatch(Handler handler) { if (initField == null) handler.saveDynamicFieldTemplate(value) ;  else handler.saveDynamicFieldTemplate(value, initField) ;  }  @Override public com.google.gwt.event.shared.GwtEvent.Type<Handler> getAssociatedType() { return TYPE ;  } }