package org.imogene.epicam.client.event.save ; public class SaveLotEvent extends GwtEvent<SaveLotEvent.Handler> { public static final Type<Handler> TYPE = new Type<Handler>() ;  public interface Handler extends EventHandler { void saveLot(LotProxy value) ;  void saveLot(LotProxy value, String initField) ;  }  private final LotProxy value ;  private final String initField ;  public SaveLotEvent(LotProxy value) { this(value, null) ;  }  public SaveLotEvent(LotProxy value, String initField) { this.value = value ;  this.initField = initField ;  }  @Override protected void dispatch(Handler handler) { if (initField == null) handler.saveLot(value) ;  else handler.saveLot(value, initField) ;  }  @Override public com.google.gwt.event.shared.GwtEvent.Type<Handler> getAssociatedType() { return TYPE ;  } }