package org.imogene.epicam.client.event.save ; public class SaveMedicamentEvent extends GwtEvent<SaveMedicamentEvent.Handler> { public static final Type<Handler> TYPE = new Type<Handler>() ;  public interface Handler extends EventHandler { void saveMedicament(MedicamentProxy value) ;  void saveMedicament(MedicamentProxy value, String initField) ;  }  private final MedicamentProxy value ;  private final String initField ;  public SaveMedicamentEvent(MedicamentProxy value) { this(value, null) ;  }  public SaveMedicamentEvent(MedicamentProxy value, String initField) { this.value = value ;  this.initField = initField ;  }  @Override protected void dispatch(Handler handler) { if (initField == null) handler.saveMedicament(value) ;  else handler.saveMedicament(value, initField) ;  }  @Override public com.google.gwt.event.shared.GwtEvent.Type<Handler> getAssociatedType() { return TYPE ;  } }