package org.imogene.epicam.client.event.save ; public class SaveOutBoxEvent extends GwtEvent<SaveOutBoxEvent.Handler> { public static final Type<Handler> TYPE = new Type<Handler>() ;  public interface Handler extends EventHandler { void saveOutBox(OutBoxProxy value) ;  void saveOutBox(OutBoxProxy value, String initField) ;  }  private final OutBoxProxy value ;  private final String initField ;  public SaveOutBoxEvent(OutBoxProxy value) { this(value, null) ;  }  public SaveOutBoxEvent(OutBoxProxy value, String initField) { this.value = value ;  this.initField = initField ;  }  @Override protected void dispatch(Handler handler) { if (initField == null) handler.saveOutBox(value) ;  else handler.saveOutBox(value, initField) ;  }  @Override public com.google.gwt.event.shared.GwtEvent.Type<Handler> getAssociatedType() { return TYPE ;  } }