package org.imogene.epicam.client.event.save ; public class SaveRegionEvent extends GwtEvent<SaveRegionEvent.Handler> { public static final Type<Handler> TYPE = new Type<Handler>() ;  public interface Handler extends EventHandler { void saveRegion(RegionProxy value) ;  void saveRegion(RegionProxy value, String initField) ;  }  private final RegionProxy value ;  private final String initField ;  public SaveRegionEvent(RegionProxy value) { this(value, null) ;  }  public SaveRegionEvent(RegionProxy value, String initField) { this.value = value ;  this.initField = initField ;  }  @Override protected void dispatch(Handler handler) { if (initField == null) handler.saveRegion(value) ;  else handler.saveRegion(value, initField) ;  }  @Override public com.google.gwt.event.shared.GwtEvent.Type<Handler> getAssociatedType() { return TYPE ;  } }