package org.imogene.epicam.server.locator ; public class SortieLotLocator extends Locator<SortieLot, String> { private SortieLotHandler handler ;  public SortieLotLocator() { }  @Override public SortieLot create(Class<? extends SortieLot> clazz) { return new SortieLot() ;  }  @Override public SortieLot find(Class<? extends SortieLot> clazz, String id) { if (handler == null) initHandler() ;  return handler.getById(id) ;  }  @Override public Class<SortieLot> getDomainType() { return SortieLot.class ;  }  @Override public String getId(SortieLot domainObject) { return domainObject.getId() ;  }  @Override public Class<String> getIdType() { return String.class ;  }  @Override public Object getVersion(SortieLot domainObject) { return domainObject.getVersion() ;  }  private void initHandler() { HttpServletRequest request = RequestFactoryServlet .getThreadLocalRequest() ;  ApplicationContext context = WebApplicationContextUtils .getWebApplicationContext(request.getSession() .getServletContext()) ;  handler = (SortieLotHandler) context.getBean("sortieLotHandler") ;  } }